import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { Form } from "react-bootstrap";

const SelectForm = (handleChange) => (
  <div>
    <Form>
      <Form.Group controlId="exampleForm.ControlSelect1">
        <Form.Control 
          as="select"
          onChange={handleChange}
          name="todoCategory"
          >
          <option>Select a category</option>
          <option>Science</option>
          <option>Sports</option>
          <option>Java</option>
          <option>Python</option>
        </Form.Control>
      </Form.Group>
    </Form>
  </div>
);

export const SelectTodo = () => {

  const handleChange = (event) => {
    event.persist();
    const target = event.target;
    setInputs((inputs) => ({ ...inputs, [target.name]: value }));

    const status = value ? 'completed': 'pending';
    console.log(target.name, value);
    dispatch(toggleTodoStatus(target.name, status));
  };

  return (
    <>
      <SelectForm handleChange={handleChange}/>
    </>
  );
};
