import React, { useState } from "react";
import { useDispatch } from 'react-redux';
import { Button, Modal, OverlayTrigger, Tooltip, Col, Row, Form } from 'react-bootstrap';
import {
  addNewTodo
} from './todoSlice';


const INITIAL_STATE = {
  category: '',
  description: '',
  dueDate: ''
};

export const AddTodo = () => {
  const dispatch = useDispatch();
  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const [inputs, setInputs] = useState(INITIAL_STATE)

  const handleChange = (event) => {
    event.preventDefault();
    const target = event.target
    const value = target.value;
    setInputs((inputs) => ({ ...inputs, [target.name]: value }));
  }

  const handleSubmit = (e) => {
    e.preventDefault();
    const { description, category, dueDate } = inputs
    const content = {
      category,
      description,
      dueDate
    }
    return dispatch(addNewTodo(content))
  }

  return (
    <>
      <Row className="mt-5">
      <Col></Col>
          <Col md={2} xs={6} className="ml-2" onClick={handleShow}>
          <OverlayTrigger overlay={<Tooltip id="tooltip-disabled">Add Todo</Tooltip>}>
            <span className="d-inline-block">
              <Button variant="outline-primary" className="btn-add" style={{ pointerEvents: 'none' }}>+</Button>{' '}
            </span>
          </OverlayTrigger>
          </Col>
        </Row>
      <Modal
        show={show}
        onHide={handleClose}
        backdrop="static"
        keyboard={false}
      >
        <Modal.Header closeButton>
          <Modal.Title>Add Todo</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group as={Row} controlId="formPlaintextPassword">
            <Form.Label column sm="2">
              Category:
            </Form.Label>
            <Col sm="10">
              <Form.Control 
                type="text"
                placeholder="Category"
                name="category"
                value={inputs.category}
                onChange={handleChange}
                />
            </Col>
          </Form.Group>
          <Form.Group as={Row} controlId="exampleForm.ControlTextarea1">
            <Form.Label column sm="2">Details:</Form.Label>
            <Col sm="10">
              <Form.Control 
                as="textarea"
                rows={3}
                name="description"
                value={inputs.description}
                onChange={handleChange}
              />
            </Col>
          </Form.Group>
          <Form.Group as={Row} controlId="formPlaintextPassword">
            <Form.Label column sm="2">
              Date:
            </Form.Label>
            <Col sm="10">
              <Form.Control 
                type="date"
                placeholder="date"
                name="dueDate"
                value={inputs.dueDate}
                onChange={handleChange}
              />
            </Col>
          </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleSubmit}>Submit</Button>
        </Modal.Footer>
      </Modal>
    </>
  );
};
