import { ADD_TODO, DELETE_TODO,  GET_ALL_TODO } from "../actionTypes";

const intitialState = {
  todoList: [],
  oneTodo: {}
}

export default (state=intitialState, action) => {
  switch(action.type) {
    case ADD_TODO: {
      const {id, content } = action.payload;
      return {
        ...state,
        todoList: [...state.todoList, id],
        oneTodo: {
          ...state.oneTodo,
          [id]: {
            content, 
            completed: false
          }
        }
      }
    }
    default:
      return state;
  }

}
