import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { Form } from "react-bootstrap";
import {getAllTodos } from "./todoSlice"

const SelectForm = ({input, handleChange}) => (
  <div>
    <Form>
      <Form.Group controlId="exampleForm.ControlSelect1">
        <Form.Control 
          as="select"
          onChange={handleChange}
          name="todoCategory"
          value={input}
          >
          <option>Select a category</option>
          <option>Science</option>
          <option>Sports</option>
          <option>Java</option>
          <option>Python</option>
        </Form.Control>
      </Form.Group>
    </Form>
  </div>
);

export const SelectTodo = () => {
  const dispatch = useDispatch();
  const intitialState = {
    value: ''
  }
  const [input, setInputs] = useState(intitialState)

  const handleChange = event => {
    event.persist();
    const target = event.target;
    // console.log(target.value);
    setInputs({value: {value: target.value}});
    // console.log(target.name, input);
    dispatch(getAllTodos(input));
  };

  return (
    <>
      <SelectForm input={input} handleChange={handleChange}/>
    </>
  );
};
